	public function newLanguageProcess($fromLangId, $toLangId){
		$Qdescription = Doctrine_Query::create()
		->from('ArticlesDescription')
		->where('language_id = ?', (int) $fromLangId)
		->execute(array(), Doctrine_Core::HYDRATE_ARRAY);
		foreach($Qdescription as $Record){
			$toTranslate = array(
				'name' => $Record['articles_name'],
				'description' => $Record['articles_description'],
			);

			EventManager::notify('ArticlesDescriptionNewLanguageProcessBeforeTranslate', $toTranslate);

			$translated = sysLanguage::translateText($toTranslate, (int) $toLangId, (int) $fromLangId);

			$newDesc = new ArticlesDescription();
			$newDesc->articles_id = $Record['articles_id'];
			$newDesc->language_id = (int) $toLangId;
			$newDesc->articles_name = $translated['name'];
			$newDesc->articles_description = $translated['description'];
			$newDesc->articles_url = $Record['articles_url'];
			$newDesc->articles_viewed = 0;

			EventManager::notify('ArticlesDescriptionNewLanguageProcessBeforeSave', $newDesc);

			$newDesc->save();
		}
	}

	public function cleanLanguageProcess($existsId){
		Doctrine_Query::create()
		->delete('ArticlesDescription')
		->whereNotIn('language_id', $existsId)
		->execute();
	}

	public function deleteLanguageProcess($langId){
		Doctrine_Query::create()
		->delete('ArticlesDescription')
		->where('language_id = ?', (int) $langId)
		->execute();
	}
}
