<?php	$lID = (int)Session::get('languages_id');	$i = 0;		function childs_category_count($categories_id) {		$categories_count = 0;		$Qcategories = Doctrine_Query::create()			->select('c.categories_id')			->from('ProductDesignerClipartCategories c')			->where('c.parent_id = ?',(int)$categories_id)			->execute();		if($Qcategories) {			foreach($Qcategories as $category) {				$categories_count++;				$categories_count += childs_category_count($category->categories_id);			}		}		return $categories_count;	} 	function get_category_tree($parent_id = '0') {		global $lID, $i;		if (childs_category_count($parent_id) > 0) {			$cat_tree = '<ul>'."\n";			$Qcategories = Doctrine_Query::create()				->select('c.*, cd.categories_name as cat')				->from('ProductDesignerClipartCategories c')				->leftJoin('c.ProductDesignerClipartCategoriesDescription cd')				->where('cd.language_id = ?', $lID)				->orderBy('c.sort_order, cd.categories_name');			$Qcategories->andWhere('c.parent_id = ?', (int)$parent_id);			$q = $Qcategories->execute();			if($q) {				foreach ($q as $subcategory) {					$i++;					$cat_tree .= '<li id="phtml_'.$i.'" ><a href="#" onclick="catclick('.$subcategory->categories_id.')"><ins>&nbsp;</ins>'.$subcategory->ProductDesignerClipartCategoriesDescription[$lID]['categories_name'].'</a>'."\n";					if (childs_category_count($subcategory->categories_id) > 0) {						$cat_tree .= get_category_tree($subcategory->categories_id);					}					$cat_tree .= '</li>'."\n";				}			}			$cat_tree .= '</ul>'."\n";		}		return $cat_tree;	}	$cat_tree = get_category_tree('0');?><p id="dialogOpener">ADD CLIPART</p><div id="dialogBox">	<div id="dialogInner">		<div class="ui-layout-west ui-widget-content" style="display: none;">			<div class="pane-header ui-state-default"><?php echo "Categories"; ?></div>			<div class="ui-layout-content">				<div class="scrolling-content">					<div id="basic_tree">						<?php echo $cat_tree; ?>					</div>				</div>			</div>		</div>		<div class="ui-layout-center ui-widget-content">			<div class="pane-header ui-state-default"><?php echo "Images"; ?></div>			<div class="ui-layout-content">				<div class="scrolling-content">					<p id="clipartimages">					</p>				</div>			</div>		</div>	</div></div>