		));
	}

	public function setUpParent(){
		$Categories = Doctrine::getTable('CustomMenusItems')->getRecordInstance();

		$Categories->hasMany('CustomMenusItemsDescription', array(
			'local' => 'menu_item_id',
			'foreign' => 'menu_item_id',
			'cascade' => array('delete')
		));
	}

	public function setTableDefinition(){
		$this->setTableName('custom_menu_items_description');

		$this->hasColumn('menu_item_id', 'integer', 4, array(
			'type'          => 'integer',
			'length'        => 4,
			'unsigned'      => 0,
			'primary'       => false,
			'autoincrement' => false
		));

		$this->hasColumn('language_id', 'integer', 4, array(
