	public function getCategoryName($menuId, $languageId){
		$Query = Doctrine_Query::create()
		->select('menu_item_name')
		->from('CustomMenusItemsDescription')
		->where('menu_item_id = ?', $menuId)
		->andWhere('language_id = ?', $languageId)
		->execute(array(), Doctrine_Core::HYDRATE_ARRAY);
		return $Query[0]['menu_item_name'];
	}

	public function newLanguageProcess($fromLangId, $toLangId){
		$Qdescription = Doctrine_Query::create()
		->from('CustomMenusItemsDescription')
		->where('language_id = ?', (int) $fromLangId)
		->execute(array(), Doctrine_Core::HYDRATE_ARRAY);
		foreach($Qdescription as $Record){
			$toTranslate = array(
				'name'            => $Record['menu_item_name'],
			);

			EventManager::notify('CustomMenusItemsDescriptionLanguageProcessBeforeTranslate', $toTranslate);

			$translated = sysLanguage::translateText($toTranslate, (int) $toLangId, (int) $fromLangId);

			$newDesc = new CustomMenusItemsDescription();
			$newDesc->menu_item_id = $Record['menu_item_id'];
			$newDesc->language_id = (int) $toLangId;
			$newDesc->menu_item_name = $translated['name'];

			EventManager::notify('CustomMenusItemsDescriptionNewLanguageProcessBeforeSave', $newDesc);

			$newDesc->save();
		}
	}

	public function cleanLanguageProcess($existsId){
		Doctrine_Query::create()
		->delete('CustomMenusItemsDescription')
		->whereNotIn('language_id', $existsId)
		->execute();
	}

	public function deleteLanguageProcess($langId){
		Doctrine_Query::create()
		->delete('CustomMenusItemsDescription')
		->where('language_id = ?', (int) $langId)
		->execute();
	}
}
